/*
const Course = ({ course}) => {
    const total = course.reduce(
        (a, b) => a + b.parts.exercises,
        0
    );
  
return (
    <div>
            {course.map(index =>
        <ul key={index.id}>
        {index.name}
         </ul>
            )} 
          {course.map(index =>
          <ul key={index.parts}>
              {console.log(index.exercises)}
              {index.exercises}
          </ul>
            )}  
            total of {total} exercises
           
    </div>
)
}
*/

const Part = (props) => {
    return (
    <div>
    {props.name} {props.exercises}
    </div>
    )
}

const Sum = (course) => {
    const sum = course.parts.map(part => part.exercises)

    return (
        <div>
      Total {sum.reduce((a,b) => a +b)} of exercises
        </div>
    )
}

const Content = (course) => {
    return (
        <div>
            {course.parts.map( index => 
            <Part key={index.id} name={index.name} exercises={index.exercises}/>  )}
        </div>
    )
}

const Header = (course) => {
    return (
<h1>
    {course.map(index =>
    <ul key={index.name}>
    </ul>
        )}
</h1>
    )
}

const Course = (course) => {  
  
    return (
        <div>
            <Header course={course} />
            <Content course={course} />
            <Sum course={course} />
        </div>
  )
}

export default Course
