/*
const Course = ({ course}) => {
    const total = course.reduce(
        (a, b) => a + b.parts.exercises,
        0
    );
  
return (
    <div>
            {course.map(index =>
        <ul key={index.id}>
        {index.name}
         </ul>
            )} 
          {course.map(index =>
          <ul key={index.parts}>
              {console.log(index.exercises)}
              {index.exercises}
          </ul>
            )}  
            total of {total} exercises
           
    </div>
)
}
*/

const Part = (props) => {
    return (
    <div>
    {props.name} {props.exercises}
    </div>
    )
}
/*
const Sum = ({course}) => {
    const sum = course.map(part => part.exercises)

    return (
        <div>
      Total {sum.reduce((a,b) => a +b)} of exercises
        </div>
    )
}
*/

const Content = ({course}) => {
    return (
        <div>
            {course.map( index => 
            <Part key={index.id} name={index.parts.name} exercises={index.parts.exercises}/>  )}
        </div>
    )
}

const Header = ({course}) => {
    const parts = course.map(index => index.parts)
    const buffer = []
    const buffer1 = []
    
   for (var i = 0; i < parts.length; i++) {
       const name = parts[i].map(index => index.name)
       const exercises = parts[i].map(index => index.exercises)
       buffer.push(<div>{name}</div>)
       buffer1.push(<div>{exercises}</div>)
   }
    return (
<div>
   {buffer.map(index => 
    <ul key={index.id} >
        <li> {index}</li>
    </ul>
    )}
    {buffer1.map(index => 
       <ul key={index.id}>
       <li> {index}</li>
       </ul>
       )}
</div>
    )
}

const Course = ({course}) => { 

  
    return (
        <div>
            <Total course={course} />
            <Header course={course} />
            <Content course={course} /> 
        </div>
  )
}

export default Course
